INSERT INTO calendar_events (title, event_type, date, time, description, significance, rituals, regional_significance, reminder_enabled) VALUES
('Maha Shivratri', 'festival', '2024-03-08', '00:00:00', 'The great night of Lord Shiva, celebrated with fasting, prayers, and night-long vigils at Shiva temples across India.', 'Commemorates the cosmic dance of Shiva and celebrates the marriage of Shiva and Parvati. Devotees seek blessings for spiritual growth and liberation.', '["fasting", "night_vigil", "abhishek", "rudra_path", "meditation"]', '["north_india", "south_india", "pan_india"]', true),

('Ram Navami', 'festival', '2024-04-17', '06:00:00', 'Celebration of the birth of Lord Rama, the seventh avatar of Vishnu, marked by prayers, processions, and readings from the Ramayana.', 'Celebrates the birth of Lord Rama, the ideal king and avatar of dharma. Devotees seek blessings for righteousness and victory over evil.', '["rama_kalyanam", "procession", "ramayana_reading", "bhajan", "charity"]', '["north_india", "ayodhya", "southern_states"]', true),

('Hanuman Jayanti', 'celebration', '2024-04-23', '05:30:00', 'Birth anniversary of Lord Hanuman, celebrated with special prayers, Hanuman Chalisa recitations, and community gatherings.', 'Honors the birth of Hanuman, the devoted follower of Rama. Celebrated for courage, strength, devotion, and protection from evil.', '["hanuman_chalisa", "special_prayers", "prasad_distribution", "strength_prayers"]', '["all_india", "hanuman_temples"]', true),

('Buddha Purnima', 'festival', '2024-05-23', '04:00:00', 'Celebrates the birth, enlightenment, and death of Gautama Buddha. Observed with meditation, prayers, and acts of charity.', 'Triple blessed day marking Buddhas birth, enlightenment under the Bodhi tree, and his passing into Nirvana.', '["meditation", "dharma_talk", "charity", "temple_visit", "buddhist_prayers"]', '["buddhist_regions", "bihar", "maharashtra", "karnataka"]', true),

('Guru Purnima', 'festival', '2024-07-21', '06:00:00', 'Day dedicated to honoring spiritual teachers and gurus. Disciples express gratitude and seek blessings from their gurus.', 'Celebrates the guru-disciple tradition in Indian culture. The day when Ved Vyasa was born, who classified the Vedas.', '["guru_puja", "dakshina_offering", "spiritual_discourse", "guru_bhajan"]', '["all_india", "spiritual_ashrams", "traditional_families"]', true),

('Krishna Janmashtami', 'festival', '2024-08-26', '00:00:00', 'Birth celebration of Lord Krishna with midnight prayers, dance performances, and elaborate decorations.', 'Celebrates the birth of Krishna, the eighth avatar of Vishnu, known for his teachings in the Bhagavad Gita.', '["midnight_prayers", "jhankis", "dahi_handi", "krishna_bhajan", "fasting"]', '["mathura", "vrindavan", "gujarat", "maharashtra"]', true),

('Ganesh Chaturthi', 'festival', '2024-09-07', '06:30:00', 'Festival celebrating Lord Ganesha with elaborate decorations, prayers, and immersion processions lasting 11 days.', 'Honors Ganesha, the remover of obstacles and lord of beginnings. Celebrated for wisdom, prosperity, and success.', '["ganesh_installation", "daily_prayers", "modak_offering", "immersion_procession"]', '["maharashtra", "karnataka", "andhra_pradesh", "tamil_nadu"]', true),

('Navaratri Beginning', 'festival', '2024-10-03', '06:00:00', 'Nine-night festival dedicated to Goddess Durga, celebrated with dance, music, fasting, and devotional activities.', 'Celebrates the divine feminine energy and the victory of Goddess Durga over the demon Mahishasura.', '["garba_dance", "durga_puja", "fasting", "kalash_sthapana", "goddess_prayers"]', '["gujarat", "west_bengal", "north_india", "eastern_states"]', true);

